require([
        'jquery',
        'fancybox',
        'domReady!'
    ], function ($, fancybox) {

        $('.fancybox').fancybox({
              buttons : [ 
                'slideShow',
                'share',
                'zoom',
                'fullScreen',
                'close'
              ],
              thumbs : {
                autoStart : true
              }
        });
});
